mapper.select:
select count(1)
from ums_role


        and id = #{id}


        and name = #{name}


        and description = #{description}


        and admin_count = #{adminCount}


        and create_time = #{createTime}


        and status = #{status}


        and sort = #{sort}

select
  id, name, description, admin_count, create_time, status, sort
from ums_role


        and id = #{id}


        and name = #{name}


        and description = #{description}


        and admin_count = #{adminCount}


        and create_time = #{createTime}


        and status = #{status}


        and sort = #{sort}
