mapper.select:
select count(1)
from ums_admin_login_log


        and id = #{id}


        and admin_id = #{adminId}


        and create_time = #{createTime}


        and ip = #{ip}


        and address = #{address}


        and user_agent = #{userAgent}

select
  id, admin_id, create_time, ip, address, user_agent
from ums_admin_login_log


        and id = #{id}


        and admin_id = #{adminId}


        and create_time = #{createTime}


        and ip = #{ip}


        and address = #{address}


        and user_agent = #{userAgent}
