mapper.select:
select count(1)
from ums_resource


        and id = #{id}


        and category_id = #{categoryId}


        and create_time = #{createTime}


        and name = #{name}


        and url = #{url}


        and description = #{description}

select
  id, category_id, create_time, name, url, description
from ums_resource


        and id = #{id}


        and category_id = #{categoryId}


        and create_time = #{createTime}


        and name = #{name}


        and url = #{url}


        and description = #{description}
